<?php if (!defined('THINK_PATH')) exit();?><!DOCTYPE HTML PUBLIC "-//W3C//DTD HTML 4.01 Transitional//EN" "http://www.w3.org/TR/html4/loose.dtd"><html><head><meta http-equiv="Content-Type" content="text/html; charset=utf-8"><title>栏目管理--网站后台管理</title><link href="__PUBLIC__/css/admin_main.css" type="text/css" rel="stylesheet" /><script src="__PUBLIC__/js/jquery.js" type="text/javascript"></script></head><body><div class="admin_nav"><ul><li><a href="__APP__/category/">栏目管理</a></li><li><a href="__APP__/page/">单页管理</a></li><li><a href="__APP__/link/">链接管理</a></li><li><a href="__APP__/article/">文章管理</a></li><li><a href="__APP__/message/">留言管理</a></li><li><a href="__APP__/index/logout">安全退出</a></li><div class="clear"></div></ul></div><div class="admin_main"><div class="admin_left"></div><div class="admin_right"><form name="link_info" method="post" action="__ACTION__">				栏目：<select name="cid"><?php if(is_array($cat_info)): foreach($cat_info as $key=>$vo): if($vo["cat_type"] == 'link'): ?><option value="<?php echo ($vo["cid"]); ?>"><?php echo ($vo["cat_name"]); ?></option><?php else: endif; endforeach; endif; ?></select><br/>网站名：<input type="text" name="link_name" /><br/>				URL地址：<input type="text" name="link_url" /><br/>				Logo：<input type="text" name="link_logo" /><input type="button" name="getLogoPath" id="uplogo" value="上传Logo" /><br/>				备注：<input type="text" name="link_other" /><br/>				排序：<input type="text" name="link_order" /><br/><input type="submit" name="submit" value="提交" /></form></div></div><div class="clear"></div></div><div class="admin_footer"></div></body></html>